import React, {Component} from "react";
import Product from '../Product/Product';
import './Carousel.css'
class Carousel extends Component {
    constructor(props){
        super(props);
        this.state = {
            products: []
        }
    }

   createProducts(){
       let products = [];
        products = this.props.products.map((ele, ind) => {
            return  <Product key={ind} product = {ele}/>
        });

        return products;
   }

   leftShift = ()=>{
       console.log(this.list.style.position);
        let left = this.list.style.left;
        left = left - 224;
        this.list.style.left = left +'px';
   }

   rightShift = ()=>{
        let left = this.list.style.left;
        if(left === 0)
        left = left + 224
        this.list.style.left = left +'px'
        console.log(left)  
    }

    render(){
        let products = this.createProducts();   

        return (
            <div className="carouselContainer">
                <div onClick={this.leftShift}> prev </div>
                <div className="list" ref = {(ref)=>{this.list = ref}}>
                    {products}
                </div>
                <div onClick={this.rightShift}> next </div>
            </div>
        );
    }
    componentDidMount(){

        console.log(  this.list , this.props.products.length);
    }

       componentWillReceiveProps(props){
           this.setState({...this.state, products: props.products});
        console.log(  this.list , this.props,props);

        this.list.style.width = `${props.products.length * 275}px` ;
       }


}
export default Carousel;
