        let obj =             {
            "sfid": "p23999",
            "sfdcName": "Bontrager Flare RT Rear Bike Light",
            "alternateName": "Bontrager Flare RT Rear Bike Light",
            "shortDesc": "Bontrager Flare RT Rear Bike Light",
            "longDesc": "<p>A compact and powerful USB rechargeable rear bike light that's daytime visible from up to 2km away.</p>",
            "parentProduct": null,
            "supersedeProduct": null,
            "availabilityMessage": null,
            "ownerId": null,
            "SKU": "2399901",
