            "shippingWeight": null,
            "totalRating": null,
            "productSpecs": [],
            "productSpecIndicies": null,
            "subProdTerms": null,
            "EProductGuides": null,
            "compositeProducts": [
                {
                    "componentSku": "2399901",
                    "compositeProductId": "p23999",
                    "composite": "p23999",
                    "description": "Bontrager Flare RT Rear Bike Light",
                    "EProductMedia": {
                        "fullURI": null,
                        "fullTitle": null,
                        "fullAltMessage": null,
                        "largeURI": null,
                        "largeTitle": null,
                        "largeAltMessage": null,
                        "mediumURI": null,
                        "mediumTitle": null,
                        "mediumAltMessage": null,
                        "smallURI": "https://ccstore-z93a.oracleoutsourcing.com/ccstore/v1/images/?source=/file/v6570925467457157223/products/BontragerFlareRTTailLight_23999_A_Primary.jpg&height=300&width=300",
                        "smallTitle": "Bontrager Flare RT Rear Bike Light",
                        "smallAltMessage": "Bontrager Flare RT Rear Bike Light",
                        "thumbURI": null,
                        "thumbTitle": null,
                        "thumbAltMessage": null,
                        "sourceURI": null,
                        "sourceTitle": null,
                        "sourceAltMessage": null
                    },
                    "priceEntry": {
                        "listPrice": 59.99,
                        "salePrice": null
                    },
                    "skuInventoryItem": {
                        "sfid": null,
                        "availabilityMessage": "inStock",
                        "inventoryLocationCode": null,
                        "qtyAvailable": null,
                        "status": null,
                        "availabilityDate": null,
                        "skuItem": "2399901"
                    },
                    "variantProperties": {
                        "x_color": {
                            "type": null,
                            "code": null,
                            "name": "Black; Finish / Anodized"
                        }
                    }
                }
            ],
            "ERelatedProducts": null,
            "variants": null
        }            
        return (
                <div className="text-center">
                    <div className="carousel slide row" data-ride="carousel" data-type="multi" data-interval="4000" id="myCarousel">
                        <ol className="carousel-indicators carousel-indicators-numbers">
                            <li data-target="#carousel-example-generic" data-slide-to="0" className="active"></li>
                            <li data-target="#carousel-example-generic" data-slide-to="1"></li>
                            <li data-target="#carousel-example-generic" data-slide-to="2"></li>
                            <li data-target="#carousel-example-generic" data-slide-to="2"></li>
                            <li data-target="#carousel-example-generic" data-slide-to="2"></li>
                        </ol>  
                        <div className="carousel-inner">
                            <div className="item active">
                              <Product product={obj}/>
                            </div>
                            <div className="item">
                            <Product product={obj}/>
                            </div>
                            <div className="item">
                            <Product product={obj}/>
                            </div>
                            <div className="item">
                            <Product product={obj}/>
                            </div>
                            <div className="item">
                            <Product product={obj}/>
                            </div>
                        </div>
                    </div>
                </div>
        );
    }
    componentDidMount(){
        console.log("componentDidMount");
        jQuery(document).ready(function() {

            jQuery('.carousel[data-type="multi"] .item').each(function(){
                var next = jQuery(this).next();
                if (!next.length) {
                    next = jQuery(this).siblings(':first');
                }
                next.children(':first-child').clone().appendTo(jQuery(this));

                for (var i=0;i<2;i++) {
                    next=next.next();
                    if (!next.length) {
                        next = jQuery(this).siblings(':first');
                    }
                    next.children(':first-child').clone().appendTo(jQuery(this));
                }
            });

        });
    }
}
export default Carousel;
