            password: "oe@123"
        },
        inputUsername:"",
        inputPassword:"",
        logInMessage:""
    }

    usernameHandler = (event)=>{
        this.setState({...this.state,inputUsername: event.target.value});
    }

    passwordHandler = (event)=>{
        this.setState({...this.state,inputPassword: event.target.value});
    }

    logInHandler = () => {
       if(this.state.user.username === this.state.inputUsername && this.state.user.password === this.state.inputPassword)
       {
           alert("true");
       }
       else{
           alert("false");
       }
    }

    render(){
        return(
            <div>
                <h2>Login</h2>
