import React, {Component} from 'react';
import './login.css';
import axios from 'axios';


const logInHandler = ()=>{ 
    console.log("in login",);
    axios({
        method: 'post',
        url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
        data: {
            "username":"trupti.kashid@objectedge.com",
            "password":"Objectedge$10"
        },
        headers: {
            "Authorization":"Bearer YWRtaW46YWRtaW4=",
            'Content-Type': 'application/json'
        }

        })
        .then(function (response) {
            //handle success
            console.log(response);
        })
        .catch(function (response) {
            //handle error
            console.log(response);
    });

}
const usernameHandler = (event)=>{
    this.setState({...this.state,inputUsername: event.target.value});
}

const passwordHandler = (event)=>{
    this.setState({...this.state,inputPassword: event.target.value});
}
const login =(props)=>{

    return(
        <div className="loginForm">
            <h2>Login</h2>
            <label>Email</label>
            <input type="text"  onChange={usernameHandler}/>
            <label>password</label>
            <input type="password" onChange={passwordHandler}/>
            <input type="submit" value="LogIn" onClick={()=>logInHandler()}/>
        </div> 
    );

}

export default login;
