import React, {Component} from 'react';
import './login.css';
class Login extends Component{

    state = {
        user:{
            username: "shivam.oe@objectedge.com",
            password: "oe@123"
        },
        inputUsername:"",
        inputPassword:""
    }

    usernameHandler = (event)=>{
        this.setState({...this.state,inputUsername: event.target.value});
    }

    passwordHandler = (event)=>{
        this.setState({...this.state,inputPassword: event.target.value});
    }

    logInHandler = () => {
        console.log(this.state.inputPassword);
    }

    render(){
        return(
            <div>
                <h2>Login</h2>
                <form>
                    <input type="text" onChange={this.usernameHandler} />
                    <input type="password" onChange={this.passwordHandler} />
                    <input type="submit" value="LogIn" onClick={this.logInHandler}/>
                </form>
            </div> 
        );
    }
}

export default Login;
