import React,{Component} from 'react';
import Login from '../../components/Login/Login';
import ToastMessage from '../../components/ToastMessage/ToastMessage';
import axios from 'axios';
import './LogInPage.css'

class LogInPage extends Component{
    constructor(props){
        super(props);
        this.state = {
            LogInMessage: "",
            show : false
        }
    }

    toastMessageHandler = (message)=>{
        console.log(message,"intoast");
        this.setState({...this.state,LogInMessage: message, show: true});
        this.show = true;
    }

    logInAPI = ()=>{
        axios({
            method: 'post',
            url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
            data: {
                "username":"trupti.kashid@objectedge.com",
                "password":"Objectedge$10"
            },
            headers: {
                "Authorization":"Bearer YWRtaW46YWRtaW4=",
                'Content-Type': 'application/json'
            }

            })
            .then(function (response) {
                //handle success
                console.log("handle success",response);

            })
            .catch(function (response) {
                //handle error
                console.log(response);
        });
        this.props.toastMessageHandler("success");
    }

    render(){
        return(
            <div className="logInPage">
                {this.state.show ? <ToastMessage message = {this.state.LogInMessage}/>: null}
                <Login logInAPI = {this.logInAPI} toastMessageHandler = {(message)=>{this.toastMessageHandler(message)}}/>
                <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSo4kdHSVd5HhGRolR5Jn92_rFENn53ZWWb0oYAn6bPQsbQRPXghg&s" className="backgroundImage" alt=""/>
            </div>
        );
    }
}

export default LogInPage;
