import React, {Component} from 'react';
import './login.css';
class Login extends Component{
    state = {
        user:{
            username: "shivam.oe@objectedge.com",
            password: "oe@123"
        },
        inputUsername:"",
        inputPassword:""
    }

    usernameHandler(event){
        console.log("user: ",event.target);
    }
    passwordHandler(event){
        console.log("password: ",event);
    }

    render(){
        return(
            <div>
                <h2>Login</h2>
                <form>
                    <input type="text" onChange={this.usernameHandler} />
                    <input type="password" onChange={this.passwordHandler} />
                    <input type="submit" onClick={this.logIn}/>
                </form>
            </div> 
        );
    }
}

export default Login;
