import React, {Component} from 'react';
import './login.css';
import axios from 'axios';
class Login extends Component{
    state = {
        inputUsername:"",
        inputPassword:"",
        logInMessage:""
    }

    usernameHandler = (event)=>{
        this.setState({...this.state,inputUsername: event.target.value});
    }

    passwordHandler = (event)=>{
        this.setState({...this.state,inputPassword: event.target.value});
    }

    logInHandler = ()=>{ 
        axios({
            method: 'post',
            url: 'https://dev-bepsy-api.objectedge.com/oe_commerce_api/occ/v1/oauth/login',
            data: {
                "username":this.state.inputUsername,//"trupti.kashid@objectedge.com"
                "password":this.state.inputPassword//"Objectedge$10"
            },
            headers: {
                "Authorization":"Bearer YWRtaW46YWRtaW4=",
                'Content-Type': 'application/json'
            }

            })
            .then(function (response) {
                //handle success
                console.log(response);
            })
            .catch(function (response) {
                //handle error
                console.log(response);
        });

    }

    render(){
        return(
            <div className="loginForm">
                <h2>Login</h2>
                <label>Email</label>
                <input type="text" onChange={this.usernameHandler} />
                <label>password</label>
                <input type="password" onChange={this.passwordHandler} />
                <input type="submit" value="LogIn" onClick={this.logInHandler}/>
            </div> 
        );
    }

}

export default Login;
